insert  into `ieps_inform`(`id`,`head`,`publisher`,`role_id`,`subject`,`content`,`files`,`pubdate`,`create_time`,`update_time`) values (1,'2019年三小项目申报流程','100001',200006,'南昌航空大学','<p>第一条&nbsp;根据《中华人民共和国教育法》、《中华人民共和国高等教育法》和国家及各省（市、区）教育主管部门普通高等学校招生工作相关文件规定，结合学校的具体情况，为规范招生行为，切实维护考生合法权益，制定本章程。</p>\n\n<p>第二条&nbsp;学校基本情况</p>\n\n<p>1、学校全称：南昌航空大学</p>\n\n<p>2、院校国标码：10406</p>\n\n<p>3、本部校址：江西省南昌市丰和南大道696号</p>\n\n<p>4、职教本科合作实验班合作的高职院校和学生就读学校校址：</p>\n\n<p>江西青年职业学院：江西省南昌市昌北经济技术开发区志敏大道1458号。</p>\n\n<p>5、办学层次：研究生、普通本科</p>\n\n<p>6、办学类型：公办、全日制普通高等院校</p>\n\n<p>7、上级主管部门：江西省教育厅</p>\n\n<p>第三条&nbsp;学校培养的本科生，毕业时所颁发学历证书的学校名称均为南昌航空大学，证书种类为普通高等教育毕业证书；对符合学士学位授予条件的本科毕业生，授予学士学位并颁发学位证书。</p>\n',NULL,'2020-01-02 14:17:40','2020-01-02 14:17:40','2020-01-02 14:17:40');

